import React, { useEffect, useState } from "react";
import { fetchChecklistResults } from "../services/apiService";

const ChecklistDashboard = () => {
  const [checklist, setChecklist] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const getChecklist = async () => {
      try {
        const results = await fetchChecklistResults();
        setChecklist(results);
      } catch (err) {
        setError(err.message);
      }
    };

    getChecklist();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div style={styles.container}>
      <h1>Checklist Dashboard</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>Rule</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {checklist.map((item, index) => (
            <tr key={index}>
              <td>{item.rule}</td>
              <td style={item.status === "Passed" ? styles.pass : styles.fail}>
                {item.status}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
    textAlign: "center",
  },
  table: {
    margin: "20px auto",
    borderCollapse: "collapse",
    width: "80%",
  },
  pass: {
    color: "green",
  },
  fail: {
    color: "red",
  },
  tableHeader: {
    fontWeight: "bold",
  },
};

export default ChecklistDashboard;
